import { WithUndefinedAsOptional } from './optionalkey';
export type GuardToType<Guard> = Guard extends (p1: unknown) => p1 is infer Type ? Type : never;
export type DestructureGuardedTypeObj<Obj extends {
    [key: string]: (p1: unknown) => p1 is unknown;
}> = WithUndefinedAsOptional<{
    [key in keyof Obj]: GuardToType<Obj[key]>;
}>;
export type Validator<TO extends FROM, FROM = unknown> = (p1: FROM) => p1 is TO;
